<?phpnamespace artjom\ToDoListBundle\Form\Extension\Type; use Symfony\Component\Form\AbstractType;use Symfony\Component\OptionsResolver\OptionsResolverInterface; class TaskStatusType extends AbstractType{		public function setDefaultOptions(OptionsResolverInterface $resolver)    {        $resolver->setDefaults(array(            'choices' => TaskStatusChoices::getChoices()        ));    }     /**     * {@inheritdoc}     */    public function getParent()    {        return 'choice';    }     /**     * {@inheritdoc}     */    public function getName()    {        return 'taskstatus';    } }